package jp.bcat;import java.io.*;public class DeleteCommand implements CLICommand {	protected BookCatalog catalog;	public DeleteCommand(BookCatalog catalog) {		this.catalog = catalog;	}		public void process(BufferedReader in, PrintWriter out) throws IOException {		out.print("íœ‚·‚é}‘ID: ");		out.flush();		String no = in.readLine();		Book book = catalog.getBook(no);		if (book == null) {			out.println("ŠY“–‚·‚é}‘‚ª‚ ‚è‚Ü‚¹‚ñB");			return;		}		new BookWriter(out, "- ").write(book);		out.print("‚±‚Ì}‘î•ñ‚ğíœ‚µ‚Ü‚·‚©H(y/n)[n]: ");		out.flush();		if (! "y".equals(in.readLine()))			return;		catalog.deleteBook(no);		out.println("íœ‚µ‚Ü‚µ‚½B");	}	public static void main(String args[]) {		try {			BufferedReader in = new BufferedReader(					new InputStreamReader(System.in));			PrintWriter out = new PrintWriter(					new OutputStreamWriter(System.out), true);			BookCatalog catalog = new BookCatalog();			DeleteCommand command = new DeleteCommand(catalog);			command.process(in, out);		} catch (Exception e) {			e.printStackTrace();		}	}}